import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import Emoji from "react-emoji-render";

// ReactDOM.render(
//   <React.StrictMode>
//     <App />
//   </React.StrictMode>,
//   document.getElementById('root')
// );

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}


class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill(null),
      XIsNext:  true
    };
  }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if(this.state.XIsNext){
      squares[i] = 'ðŸ˜€';
    } else {
      squares[i] = 'ðŸ˜ˆ'
    }
    this.setState({squares: squares,
    XIsNext: !this.state.XIsNext});
  }

  renderSquare(i) {
    return <Square value={this.state.squares[i]}
    onClick={() => this.handleClick(i)} />;
  }

  render() {
    const status = 'Next player: ' + (this.state.XIsNext? 'ðŸ˜€' : 'ðŸ˜ˆ');

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

function SelectionSquare(props) {
  return (
    <button className="selsquare" onClick={props.onClick}>
      {props.value}
    </button>
  );
}

class Selection extends React.Component {
  handleClick(i,n) {
    if(i==1 && this.state.xEmoji==null) {
      this.state = {
        xEmoji: i,
        oEmoji: this.state.oEmoji,
        gameStarted: Boolean(i && this.state.oEmoji)
      }
    } else if (i==2 && this.state.oEmoji==null) {
      this.state = {
        xEmoji: this.state.xEmoji,
        oEmoji: i,
        gameStarted: Boolean(i && this.state.xEmoji)
      } 
    }
  }

  renderSquare(i,n) {
    return <SelectionSquare value ={i}
    onClick={() => this.handleClick(i,n)} />;
  }

  render() {
    return (
      <div>
      <div className="board-row">
            {this.renderSquare('ðŸ˜€',1)}
            {this.renderSquare('ðŸ˜ˆ',1)}
            {this.renderSquare('ðŸ’©',1)}
            {this.renderSquare('ðŸ˜€',2)}
            {this.renderSquare('ðŸ˜ˆ',2)}
            {this.renderSquare('ðŸ’©',2)}
      </div>
      </div>
    );
   }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      xEmoji : null,
      oEmoji : null,
      gameStarted : false
    };
  }

  render() {
    if (!this.state.gameStarted) {
      return(
        <div className="game">
          <div className="game-board">
            <Selection />  
          </div>  
        </div>
      );
    } else {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
