import React from 'react';
import '../index.css';
import { Square } from './square.js'
import { SelectionSquare } from './selectionsquare.js'
import { calculateWinner } from './functions.js'

export class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      gameReady: false,
      gameStarted: false,
      xEmoji: "",
      oEmoji: "",
      squares: Array(9).fill(null),
      XIsNext: true,
      Winner: null
    };
  }

  selClick(i, n) {
    if (n === 1 && !(this.state.oEmoji === i)) {
      this.setState({
        xEmoji: i,
        oEmoji: this.state.oEmoji,
        gameReady: Boolean(i && this.state.oEmoji)
      });
    }
    if (n === 2 && !(this.state.xEmoji === i)) {
      this.setState({
        xEmoji: this.state.xEmoji,
        oEmoji: i,
        gameReady: Boolean(i && this.state.xEmoji)
      });
    }
  }

  startClick() {
    this.setState({
      gameStarted: true
    })
  }

  renderSquare(i) {
    return <Square value={this.state.squares[i]}
      onClick={() => this.handleClick(i)} />;
  }

  renderSelSquare(i, n) {
    return <SelectionSquare value={i}
      onClick={() => this.selClick(i, n)} />;
  }

  renderStartSquare() {
    return <Square value={"Start Game"}
      onClick={() => this.startClick()} style={{ width: "1000px", fontSize: "10px" }} />
  }

  handleClick(i) {
    const squares = this.state.squares.slice();
    if (!this.state.squares[i]) {
      if (this.state.XIsNext) {
        squares[i] = this.state.xEmoji;
      } else {
        squares[i] = this.state.oEmoji
      }
      this.setState({
        squares: squares,
        XIsNext: !this.state.XIsNext,
        Winner: calculateWinner(squares)
      });
      console.log(squares)
      console.log("winner is " + calculateWinner(squares))
      console.log(this.state)
      //alert('The winner is' + calculateWinner(squares))
    }
  }

  render() {
    if (!this.state.gameStarted) {
      return (
        <div className="main">
          <div>
            Emoji Selection
          </div>
          <div className="board-row">
            {this.renderSelSquare('ðŸ˜€', 1)}
            {this.renderSelSquare('ðŸ˜ˆ', 1)}
            {this.renderSelSquare('ðŸ’©', 1)}
            {this.renderSelSquare('ðŸ˜€', 2)}
            {this.renderSelSquare('ðŸ˜ˆ', 2)}
            {this.renderSelSquare('ðŸ’©', 2)}
          </div>
          <div>
            Human {this.state.xEmoji.toString()}
          </div>
          <div>
            Computer {this.state.oEmoji.toString()}
          </div>
          {this.state.gameReady && this.renderStartSquare()}
        </div>
      );
    }
    else {
      const status = 'Next player: ' + (this.state.XIsNext ? this.state.xEmoji : this.state.oEmoji)
      return (
        <div className="main2">
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }
}
